INSERT INTO product(type_of_product_id, supplier_id, name, description, quentity_per_unit, unit_price, units_in_stock, units_in_order, reorder_level) VALUES(1, 1, 'Caykur Rize Turist Cayi 500g', 'Herbata Caykur Rize Turist Cayi pochodzi z tradycyjnych upraw znajdujących się w północno-wschodniej części Turcji, w okręgu Rize nad Morzem Czarnym. Przy wspomnianych uprawach nie stosuje się pestycydów, a zebrane liście herbaty zostają poddane kruszeniu, co wpływa na krótszy czas parzenia napoju. Napar Caykur Rize jest jasny o miedzianej barwie, a jego aromat można określić jako świeży i delikatny. Herbata czarna Caykur Rize Turist Cayi zdecydowanie należy do herbat, które można pić przez cały dzień.', '500', 32.00, 100, 0, 50);
INSERT INTO product(type_of_product_id, supplier_id, name, description, quentity_per_unit, unit_price, units_in_stock, units_in_order, reorder_level) VALUES(1, 1, 'Caykur Earl Grey 125g', 'Poczuj klasyczny smak mocnej, tureckiej herbaty z dodatkiem bergamotki. Czarna herbata Caykur Earl Grey nie zawiera pestycydów, a jej napar jest pomarańczowo-miedziany, o intensywnym, głębokim aromacie bergamotki i o cytrusowym smaku. Napój ten charakteryzuje się drobno pokruszonymi liśćmi, z których otrzymuje się niepowtarzalny, mocny napar.', '125g', 25.69, 100, 0, 50);
INSERT INTO product(type_of_product_id, supplier_id, name, description, quentity_per_unit, unit_price, units_in_stock, units_in_order, reorder_level) VALUES(1, 1, 'Caykur Filiz Cayi 500g', 'Ta turecka czarna herbata pochodzi wprost z tradycyjnych upraw rozciągających się wokół portu Rize, położonego na północnym-wschodzie Turcji. Herbata czarna Caykur Filiz Cayi nie zawiera pestycydów, w związku z czym jej smak jest bardzo naturalny. Napar ten posiada jasnobrązową barwę, a jego aromat jest intensywny. Delikatnie kwaskowaty posmak, który posiada ta czarna herbata, równoważy towarzysząca mu długa słodycz.', '500g', 34.00, 100, 0, 50);
INSERT INTO product(type_of_product_id, supplier_id, name, description, quentity_per_unit, unit_price, units_in_stock, units_in_order, reorder_level) VALUES(1, 3, 'Twinings English Breakfast 100g', 'Jasna, mocna o bursztynowym kolorze, szczyci się połączeniem odświeżającego i wzmacniającego smaku. Pomimo nazwy herbata English Breakfast jest odpowiednim napojem na każda porę dnia.', '100g', 23.00, 100, 0, 50);